import React, { Component } from 'react';

class NameForm extends Component {
  constructor(props) {
    super(props);
    this.state = {value: '',
    value1: '',
    value2: '',
    value3: '',
    value: ''


  };


    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A value was submitted: ' + this.state.value);
    alert('A value was submitted: ' + this.state.value1);
    alert('A value was submitted: ' + this.state.value2);
    alert('A value was submitted: ' + this.state.value3);
    alert('A value was submitted: ' + this.state.value4);

    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
          Last Name:
          <input type="text" value1={this.state.value1} onChange={this.handleChange} />
          Email Address:
          <input type="text" value2={this.state.value3} onChange={this.handleChange} />
          Phone Number:
          <input type="text" value3={this.state.value3} onChange={this.handleChange} />
          Rate us:
          <select value={this.state.value} onChange={this.handleChange}>
            <option value="good">Good</option>
            <option value="bad">Bad</option>
          </select>
          Comment:
          <input type="text" value4={this.state.value4} onChange={this.handleChange} />

        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

export default NameForm;
