import Main from './components/main';
import NameForm from './components/nameform';


const particleOpt = {
  particles: {
    number: {
      value: 150,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}

class App extends React.Component {
  render(){return (
    <div>


      <Header className="header-color" title={<Link style={{textDecoration: 'yes',color:'yellow' }} to="/">Looking for Sports? </Link>} scroll>
      </Header>
<div className= "nav-bar">
       <Navigation >

      <Link className ="Home" to="/City">Cities</Link>  
      {/* <Link className ="City" to="/Home">Home</Link> */}
      <Link className ="nameform" to="/nameform">Reviews</Link>

      <Content>
            <div className="page-content" />

            <Main/>

          <div/>

        </Content>
      </Navigation>
      </div >
<Particles 
         params={particleOpt} />


    </div>

  );
}

}

export default App;
