import React, { Component } from 'react';

class NameForm extends Component {
  constructor(props) {
    super(props);
    this.state = {value: '',
    value1: '',
    value2: '',
    value3: '',
    value4: ''


  };


    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
    this.setState({value1: event.target.value1});
    this.setState({value2: event.target.value2});
    this.setState({value3: event.target.value3});
    this.setState({value4: event.target.value4});


  }

  handleSubmit(event) {
    alert('A value was submitted: ' + this.state.value);
    alert('A value was submitted: ' + this.state.value1);
    alert('A value was submitted: ' + this.state.value2);
    alert('A value was submitted: ' + this.state.value3);
    alert('A value was submitted: ' + this.state.value4);

    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
           Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
          Full Name:
          <input type="text" value1={this.state.value1} onChange={this.handleChange} />
          Email Address:
          <input type="text" value2={this.state.value2} onChange={this.handleChange} />
          Phone Number:
          <input type="text" value3={this.state.value3} onChange={this.handleChange} />
          Rate us:
          <select value3={this.state.value3} onChange={this.handleChange}>
            <option value3="good">Good</option>
            <option value3="bad">Bad</option>
          </select>
          Comment:
          <input type="text" value4={this.state.value4} onChange={this.handleChange} />

        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

export default NameForm;
