  render() {
    return (
      <div className="App">
        <div className="container" ref={this.container}>
          <button type="button" class="btn btn-md" onClick={this.handleButtonClick}>

            <img className = "Image2" src ={require("./dropdown.png")}/>
          </button>
          {this.state.open && (
            <div class="container">
              <ul>
              <div class="dropdown">
  <button class="btn btn-secondary btn-lg" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
    Brooklyn
  </button>
</div>
<div class="dropdown1">
