import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';




class City extends Component {
  container = React.createRef();
  state = {
    open: false,
  };
  componentDidMount() {
    document.addEventListener("mousedown", this.handleClickOutside);
  }
  componentWillUnmount() {
    document.removeEventListener("mousedown", this.handleClickOutside);
  }
  handleClickOutside = event => {
    if (this.container.current && !this.container.current.contains(event.target)) {
      this.setState({
        open: false,
      });
    }
  };
  handleButtonClick = () => {
    this.setState(state => {
      return {
        open: !state.open,
      };
    });
  };

  testClick = () => {

    console.log('button was clicked')
  }

  render() {
    return (
      <div className="App">
        <div className="container" ref={this.container}>
          <button type="button" class="btn btn-md" onClick={this.handleButtonClick}>

            <img className = "Image2" src ={require("./dropdownpic.png")}/>
          </button>
          {this.state.open && (
            <div class="container">
              <ul>
              <div class="dropdown">
  <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false" onClick={this.testClick}>
    Brooklyn
  </button>
  {/* <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
